mapper.insert:
INSERT INTO product_image (product_id, origin_name, file_name, file_size)
VALUES (


        (SELECT id FROM product WHERE name = #{productName})


        #{product_id}

,
#{originName}, #{firebaseUrl}, #{fileSize}
)

mapper.select:
SELECT product.id, CASE product.status WHEN 1 THEN 'sale' WHEN 2 THEN 'stop sale' WHEN 3 THEN 'sold out' END AS status, product.name, product.price, product.rate, product.created_at, category.category_name FROM product product JOIN category category ON product.category_id = category.id
product.status = #{selectedStatus}
AND category_name = #{selectedCategory}
AND product.name LIKE CONCAT('%', #{searchKeyword}, '%')
LIMIT #{selectedTab}
ORDER BY product.id DESC
