mapper.select:
SELECT COUNT(*) AS total_count
FROM product product
JOIN category category ON product.category_id = category.id


        AND product.status = #{selectedStatus}


        AND category_name = #{selectedCategory}


        AND product.name LIKE CONCAT('%', #{searchKeyword}, '%')

SELECT product.id, CASE product.status WHEN 1 THEN 'sale' WHEN 2 THEN 'stop sale' WHEN 3 THEN 'sold out' WHEN 4 THEN 'deleted' END AS status, product.name, product.price, product.rate, product.created_at, product.updated_at, category.category_name FROM product product JOIN category category ON product.category_id = category.id
product.status = #{selectedStatus}
AND category_name = #{selectedCategory}
AND product.name LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY product.id DESC
LIMIT #{selectedTab}

mapper.update:
